<?phpnamespace DB\Table;use \DB\Type;class tribunal_settings extends \DB\Table{    const ORG = 'ORG';    const id = 'id';    const AJusticeOfThePeace = 'AJusticeOfThePeace';    const Signatory = 'Signatory';    public function initColumn($childClass = '')    {    // обязательно для формирования структуры массива        parent::initColumn(__CLASS__);        $this->identifierColumn($this::id);        $this->declare_type($this::ORG,                 Type::int);        $this->declare_type($this::id,                  Type::int);        $this->declare_type($this::AJusticeOfThePeace,  Type::varchar,1000);        $this->declare_type($this::Signatory,           Type::varchar,200);        $this->declare_primaryIndex($this::ORG);        $this->declare_primaryIndex($this::id);    }}